from itertools import permutations as perm
def findSubstring(s, words):

    n = len(words[0])
    hashmap = dict()
    sublen = n * len(words)
    end = len(s) - sublen + 1

    for i in range(end):
        if s[i:i+sublen] in hashmap:
            x = hashmap[s[i:i+sublen]]
            x.add(i)
        else:
            x = set()
            x.add(i)
            hashmap[s[i:i+sublen]] = x

    permList = perm(words)
    idxList = set()

    for wordList in list(permList):
        word = ''.join(wordList)
        if word in hashmap:
            [idxList.add(i) for i in hashmap[word]]
    return idxList

